import React, { useEffect, useState } from "react";
import { Pie } from "react-chartjs-2";
import { obtenerResumenIngresos } from "../../../services/api";

const InformesIngresos = () => {
    const [data, setData] = useState(null);

    useEffect(() => {
        const fetchIngresos = async () => {
            try {
                const resumen = await obtenerResumenIngresos();
                console.log(resumen)
                const labels = Object.keys(resumen); // Tipos de transacciones
                const valores = Object.values(resumen); // Montos

                // ConfiguraciÃ³n de datos para Chart.js
                setData({
                    labels,
                    datasets: [
                        {
                            label: "Ingresos por Tipo",
                            data: valores,
                            backgroundColor: [
                                "#FF6384", // Color 1
                                "#36A2EB", // Color 2
                                "#FFCE56", // Color 3
                                "#4BC0C0", // Color 4
                                "#9966FF", // Color 5
                            ],
                            hoverOffset: 4,
                        },
                    ],
                });
            } catch (error) {
                console.error("Error al obtener los ingresos:", error);
            }
        };

        fetchIngresos();
    }, []);

    if (!data) {
        return <p>Cargando ingresos...</p>;
    }

    return (
        <div className="bg-white p-4 rounded shadow">
            <h3 className="text-lg font-bold mb-4">ðŸ’° Informes de Ingresos</h3>
            <Pie data={data} />
        </div>
    );
};

export default InformesIngresos;
