import React, { useEffect, useState } from "react";
import { Pie } from "react-chartjs-2";
import { obtenerResumenIngresos } from "../../../services/api";
import {
    Chart as ChartJS,
    ArcElement,
    Tooltip,
    Legend,
} from "chart.js"; 
const InformesIngresos = () => {
    const [data, setData] = useState(null); // Datos para la grÃ¡fica
    const [transacciones, setTransacciones] = useState([]); // Lista de transacciones detalladas

    useEffect(() => {
        const fetchIngresos = async () => {
            try {
                const resumen = await obtenerResumenIngresos();

                // Procesar datos para la grÃ¡fica
                const tipos = {};
                resumen.forEach((tx) => {
                    const { tipo, monto } = tx;
                    tipos[tipo] = (tipos[tipo] || 0) + monto;
                });

                // Crear datos para la grÃ¡fica
                setData({
                    labels: Object.keys(tipos),
                    datasets: [
                        {
                            label: "Ingresos por Tipo",
                            data: Object.values(tipos),
                            backgroundColor: [
                                "#FF6384", // Color 1
                                "#36A2EB", // Color 2
                                "#FFCE56", // Color 3
                                "#4BC0C0", // Color 4
                                "#9966FF", // Color 5
                            ],
                            hoverOffset: 4,
                        },
                    ],
                });

                // Guardar transacciones completas para mostrarlas en la tabla
                setTransacciones(resumen);
            } catch (error) {
                console.error("Error al obtener los ingresos:", error);
            }
        };

        fetchIngresos();
    }, []);

    if (!data) {
        return <p>Cargando ingresos...</p>;
    }

    return (
        <div className="bg-white p-4 rounded shadow">
            <h3 className="text-lg font-bold mb-4">ðŸ’° Informes de Ingresos</h3>

            {/* GrÃ¡fica de Ingresos */}
            <div className="mb-6">
                <Pie data={data} />
            </div>

            {/* Tabla de Detalles */}
            <h4 className="text-md font-semibold mb-2">Detalles de Transacciones</h4>
            <table className="min-w-full bg-white border border-gray-200">
                <thead>
                    <tr>
                        <th className="px-4 py-2 border">Tipo</th>
                        <th className="px-4 py-2 border">Monto (â‚¬)</th>
                        <th className="px-4 py-2 border">Fecha y Hora</th>
                    </tr>
                </thead>
                <tbody>
                    {transacciones.map((tx, index) => (
                        <tr key={index}>
                            <td className="px-4 py-2 border">{tx.tipo}</td>
                            <td className="px-4 py-2 border">{tx.monto.toFixed(2)}</td>
                            <td className="px-4 py-2 border">{new Date(tx.fechaHora).toLocaleString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default InformesIngresos;
