import React, { useState } from "react";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";

const CheckoutForm = ({ onPaymentSuccess }) => {
    const stripe = useStripe();
    const elements = useElements();
    const [loading, setLoading] = useState(false);
    const [errorMessage, setErrorMessage] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setErrorMessage("");

        if (!stripe || !elements) {
            setLoading(false);
            return;
        }

        const cardElement = elements.getElement(CardElement);

        const { error, paymentIntent } = await stripe.confirmCardPayment({
            payment_method: {
                card: cardElement,
                billing_details: {
                    name: "Nombre del cliente", // Cambiar si tienes un nombre real
                },
            },
        });

        if (error) {
            setErrorMessage(error.message);
            console.error("Error en el pago:", error);
        } else if (paymentIntent?.status === "succeeded") {
            alert("¡Pago completado con éxito!");
            onPaymentSuccess(); // Llama a la función de éxito
        }

        setLoading(false);
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-4">
            <div>
                <label className="block text-gray-700 font-bold mb-2">Información de la Tarjeta</label>
                <CardElement className="border border-gray-300 p-2 rounded" />
            </div>
            {errorMessage && <p className="text-red-500 text-sm">{errorMessage}</p>}
            <button
                type="submit"
                disabled={!stripe || loading}
                className={`w-full bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600 ${
                    loading ? "opacity-50 cursor-not-allowed" : ""
                }`}
            >
                {loading ? "Procesando..." : "Pagar"}
            </button>
        </form>
    );
};

export default CheckoutForm;
