import React, { useState, useEffect } from "react";
import { obtenerCitasPendientesPago } from "../../../services/api";

const CitasPendientesPago = () => {
    const [citas, setCitas] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchCitasPendientes = async () => {
            try {
                const data = await obtenerCitasPendientesPago();
                setCitas(data);
                setLoading(false);
            } catch (error) {
                setError("Error al cargar las citas pendientes de pago.");
                setLoading(false);
            }
        };

        fetchCitasPendientes();
    }, []);

    if (loading) return <p>Cargando citas pendientes de pago...</p>;
    if (error) return <p className="text-red-500">{error}</p>;

    return (
        <div className="bg-white p-4 rounded shadow">
            <h3 className="text-lg font-bold mb-2">Citas Pendientes de Pago</h3>
            {citas.length > 0 ? (
                <ul>
                    {citas.map((cita) => (
                        <li key={cita.idReserva} className="mb-2">
                            <span className="block text-gray-700 font-semibold">
                                Cliente: {cita.cliente.nombre}
                            </span>
                            <span className="block text-gray-600">
                                Servicio: {cita.servicio.nombre} - {cita.fecha} {cita.horaInicio}
                            </span>
                            <span className="block text-gray-600">
                                Total: {cita.precioTotal.toFixed(2)} €
                            </span>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No hay citas pendientes de pago.</p>
            )}
        </div>
    );
};

export default CitasPendientesPago;
