import React, { useEffect } from "react";
import { useSearchParams, useNavigate } from "react-router-dom";
import { crearReserva } from "../services/api";

const SuccessPage = () => {
    const [searchParams] = useSearchParams();
    const navigate = useNavigate();

    const reserva = JSON.parse(localStorage.getItem("reservaTemporal"));

    useEffect(() => {
        const guardarReservaBackend = async () => {
            try {
                if (searchParams.get("payment_intent") && reserva) {
                    await crearReserva(reserva);
                    reserva.
                    alert("Reserva guardada correctamente.");
                    localStorage.removeItem("reservaTemporal");
                    navigate("/dashboard"); // Redirige al dashboard u otra página
                }
            } catch (error) {
                console.error("Error al guardar la reserva:", error);
                alert("Hubo un problema al guardar la reserva.");
            }
        };

        guardarReservaBackend();
    }, [searchParams, reserva, navigate]);

    return (
        <div className="p-6 max-w-lg mx-auto bg-white rounded shadow-lg">
            <h1 className="text-2xl font-bold mb-4">¡Pago Realizado con Éxito!</h1>
            <p className="mb-4">Gracias por realizar el pago. Tu reserva ha sido confirmada.</p>
        </div>
    );
};

export default SuccessPage;
