import React from "react";

const HeroSection = () => {
    return (
        <div className="relative bg-gray-100">
            {/* Imagen superior */}
            <div className="w-full h-64">
                <img
                    src="https://img.freepik.com/fotos-premium/abstracto-borroso-clinica-hospital-interior-medico-fondo-foto-archivo-oficina-fondos-clinica-medica-hospital-desenfocado_1028938-482131.jpg"
                    alt="Clínica"
                    className="w-full h-full object-cover"
                />
            </div>

            {/* Texto principal */}
            <div className="relative bg-blue-500 text-white text-center py-20 px-4">
                <div className="absolute inset-0 bg-black bg-opacity-30"></div>
                <div className="relative z-10">
                    <h1 className="text-5xl font-bold mb-4">Bienvenido a Nuestra Clínica</h1>
                    <p className="text-lg mb-6">
                        Ofrecemos los mejores servicios para tu salud y bienestar. ¡Conócenos!
                    </p>
                    <button className="bg-white text-blue-500 font-semibold py-2 px-6 rounded hover:bg-gray-100">
                        Explorar Servicios
                    </button>
                </div>
            </div>
        </div>
    );
};

export default HeroSection;
