import React from "react";

const ServiciosDestacados = () => {
    const servicios = [
        {
            img: "https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.vivomisalud.com%2Fconsulta-siempre-con-tu-medico%2F&psig=AOvVaw2KRo8nQ5p86YxWQjLLik9n&ust=1737100282403000&source=images&cd=vfe&opi=89978449&ved=2ahUKEwiN87j74PmKAxVEckEAHUopBfYQjRx6BAgAEBk", // Reemplazar con la URL
            titulo: "Consulta Médica",
            descripcion: "Asesoramiento y atención personalizada por nuestros médicos expertos.",
        },
        {
            img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQctjxcHaFXYzsthuTUX1tFA354g99H11dG9wkTFEKJkhl3NPcLGooronzU6IRyAyOYe_I&usqp=CAU", // Reemplazar con la URL
            titulo: "Diagnósticos Avanzados",
            descripcion: "Equipamiento moderno para análisis clínicos rápidos y efectivos.",
        },
        {
            img: "https://www.google.com/imgres?q=terapias%20personalizadas&imgurl=https%3A%2F%2Fs1.ppllstatics.com%2Ftodoalicante%2Fwww%2Fmultimedia%2F2024%2F09%2F06%2Frehabilitacion-denia-kJEG-U2201162205022w9G-1200x840%40TodoAlicante.jpg&imgrefurl=https%3A%2F%2Fwww.todoalicante.es%2Fsanidad%2Fhospital-denia-oferta-terapias-personalizadas-pacientes-insuficiencia-20240906130246-nt.html&docid=_HmE1zOPUyIZAM&tbnid=09h4C3jmKAb6MM&vet=12ahUKEwjF2bzR4PmKAxVMQvEDHcjyIDMQM3oECHQQAA..i&w=1200&h=840&hcb=2&ved=2ahUKEwjF2bzR4PmKAxVMQvEDHcjyIDMQM3oECHQQAA", // Reemplazar con la URL
            titulo: "Terapias Personalizadas",
            descripcion: "Recupérate con tratamientos adaptados a tus necesidades.",
        },
    ];

    return (
        <div className="py-12 px-4 bg-gray-100">
            <h2 className="text-3xl font-bold text-center text-blue-500 mb-8">
                Nuestros Servicios Destacados
            </h2>
            <div className="flex flex-wrap justify-center gap-6">
                {servicios.map((servicio, index) => (
                    <div
                        key={index}
                        className="bg-white p-6 rounded shadow text-center max-w-sm"
                    >
                        {/* Imagen del servicio */}
                        <img
                            src={servicio.img}
                            alt={servicio.titulo}
                            className="w-full h-40 object-cover rounded mb-4"
                        />
                        <h3 className="text-lg font-bold">{servicio.titulo}</h3>
                        <p className="text-gray-600">{servicio.descripcion}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ServiciosDestacados;
