import React, { useEffect, useState } from "react";
import { obtenerTasaPagosPendientes } from "../../../services/api";

const EstadisticasGenerales = () => {
    const [estadisticas, setEstadisticas] = useState({
        tasaPendientes: 0,
        totalCitas: 0,
        citasPendientesPago: 0,
    });

    useEffect(() => {
        const fetchEstadisticas = async () => {
            try {
                const data = await obtenerTasaPagosPendientes();
                setEstadisticas(data);
            } catch (error) {
                console.error("Error al cargar las estadÃ­sticas:", error);
            }
        };

        fetchEstadisticas();
    }, []);

    return (
        <div className="bg-white p-4 rounded shadow">
            <h3 className="text-lg font-bold mb-2">ðŸ“Š EstadÃ­sticas Generales</h3>
            <p className="text-gray-600">Citas hoy: 25</p>
            <p className="text-gray-600">Pacientes nuevos esta semana: 10</p>
            <p className="text-gray-600">Doctores activos: 8</p>
            <p className="text-gray-600">
                Tasa de Pagos Pendientes: {estadisticas.tasaPendientes.toFixed(2)}%
            </p>
            <p className="text-gray-600">
                Citas Pendientes de Pago: {estadisticas.citasPendientesPago} /{" "}
                {estadisticas.totalCitas}
            </p>
            <button className="bg-black text-white py-2 px-4 mt-4 rounded hover:bg-gray-800">
                Ver Reportes Detallados
            </button>
        </div>
    );
};

export default EstadisticasGenerales;
